import React, { useEffect, useState } from 'react'
import './ApartmentPage.scss'
import { ImageBanner } from '../components/ImageBanner'
import { ApartmentHeader } from '../components/ApartmentHeader'
import { DescriptionPanel } from '../components/DescriptionPanel'
import { useLocation } from 'react-router-dom'

function ApartmentPage() {
  const location = useLocation()
  const [selectedFlat, setSelectedFlat] = useState(null)
  useEffect(fetchApartmentsData, [location.state.apartmentId]) // Ajout de location.state.apartmentId

  function fetchApartmentsData() {
  fetch("data.json")
    .then((res) => res.json())
    .then((flats) => {
      const flat = flats.find((flat) => flat.id === location.state.apartmentId)
      setSelectedFlat(flat)
    })
    .catch(console.error)
  }
if (selectedFlat == null) return <div> ...Loading</div>
  return (
    <div className='apartment-page'>
        selected flat:{JSON.stringify(selectedFlat)}
        <ImageBanner imageURL={selectedFlat.cover} />
        <ApartmentHeader />
        <div className='apartment__description__area'>
            <DescriptionPanel />
            <DescriptionPanel />
        </div>
    </div>
  )
}

export default ApartmentPage
